import { useState } from "react"

export default function Home (props) {

   let userName = props.user
   const[cmt, setcmt] = useState(["hi", "hi"])
   useEffect( () => {
      alert("useeffect")
      Getcmt().then(cmtz => {
        if (cmtz) {
        setcm(true)
        setSessionUser(user)
        alert("user returned from chkauth", user)
        }
      })
    },);


