      },
      body: JSON.stringify(data),
      }

      let Url = 'http://192.168.1.93:4001/getCmt'
      // post request using options object
      console.time("put response took");
      let response = await fetch(Url, options)
      let convert = await response.json ()
      console.timeEnd("put response took");
      console.log("got comment is:")
      console.log(convert)

      if ( response.status === 401 || response.status === 400){// handle not a match
        alert("couldnt find comments try again")
        // Navigate("/signIn")
        //return false   
       } else if ( response.status === 200){ // if password is a match return auth'd username
        console.log("found comment")
        console.log(convert.Posts)
        setcmt(convert.Posts)
           //document.cookie = "authjwt="+convert.token; // store data in cookie
           //localStorage.setItem('jwt_token', convert.token) // Store JWT in storage
        //alert("Welcome to the club! Please setup your profile")
           //Navigate ("/profileSetup")
       }

    }

    getCmt()

   function mapIt () {
      cmt.map((cmts)=> (
         <li>
