import { useState } from "react"

export default function Home (props) {

   let userName = props.user
   const[cmt, setcmt] = useState()


   async function getCmt () { // sends username, password from input server then creates s3 bucket in username and stores details on mongo


        // data to be sent in request to api includinb id of mongodb document
      let data = {Username: userName}

      // object containing fetch options for the put request
      let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }

      let Url = 'http://192.168.1.93:4001/getCmt'
      // post request using options object
      console.time("put response took");
      let response = await fetch(Url, options)
      let convert = await response.json ()
      console.timeEnd("put response took");
      console.log("got comment is:")
      console.log(convert)

      if ( response.status === 401 || response.status === 400){// handle not a match
        alert("couldnt find comments try again")
        // Navigate("/signIn")
        //return false   
       } else if ( response.status === 200){ // if password is a match return auth'd username
        console.log("found comment")
        console.log(convert.Posts)
           //document.cookie = "authjwt="+convert.token; // store data in cookie
           //localStorage.setItem('jwt_token', convert.token) // Store JWT in storage
        //alert("Welcome to the club! Please setup your profile")
           //Navigate ("/profileSetup")
           return convert.Posts
       }

    }




   async function sendCmt () { // sends username, password from input server then creates s3 bucket in username and stores details on mongo

      const Comment = document.getElementById('cmt').value;// get username password and email

        // data to be sent in request to api includinb id of mongodb document
      let data = {Username: userName, Updatetype: '$set',
                  Key2updt: 'Posts', Value2updt: Comment
      }

      // object containing fetch options for the put request
      let options = {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }

      let postUrl = 'http://192.168.1.93:4001/postMsg'
      // post request using options object
      console.time("put response took");
      let response = await fetch(postUrl, options)
      let convert = await response.json ()
      console.timeEnd("put response took");
      console.log("added comment updated document is:")
      console.log(convert)

      if ( response.status === 401 || response.status === 400){// handle not a match
        alert("your comment wasnt added try again")
        // Navigate("/signIn")
        //return false   
       } else if ( response.status === 200){ // if password is a match return auth'd username
        console.log("pushed comment")
        console.log(convert.Posts)
        setcmt(convert.Posts)
        alert("pushed comment")
           //document.cookie = "authjwt="+convert.token; // store data in cookie
           //localStorage.setItem('jwt_token', convert.token) // Store JWT in storage
        //alert("Welcome to the club! Please setup your profile")
           //Navigate ("/profileSetup")
       }
    }


return (

<div class="container">
   <div class="row">
      <div class="col-md-12">
         <div id="content" class="content content-full-width">
            {/* begin profile */}
            <div class="profile">
               <div class="profile-header">
                  {/* BEGIN profile-header-cover */}
                  <div class="profile-header-cover"></div>
                  {/* END profile-header-cover */}
                  {/* BEGIN profile-header-content */}
                  <div class="profile-header-content">
                     {/* BEGIN profile-header-img */}
                     <div class="profile-header-img">
                        <img src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic1.jpg"} alt=""/>
                     </div>
                     {/* END profile-header-img */}
                     {/* BEGIN profile-header-info */}
                     <div class="profile-header-info">
                        <h4 class="m-t-10 m-b-5">{userName}</h4>
                        <p class="m-b-10">UXUI + Frontend Developer</p>
                        <a href="#" class="btn btn-sm btn-info mb-2">Edit Profile</a>
                     </div>
                     {/* END profile-header-info */}
                  </div>
                  {/* END profile-header-content */}
                  {/* BEGIN profile-header-tab */}
                  <ul class="profile-header-tab nav nav-tabs">
                     <li class="nav-item"><a href="#profile-post" class="nav-link active show" data-toggle="tab">POSTS</a></li>
                     <li class="nav-item"><a href="#profile-about" class="nav-link" data-toggle="tab">ABOUT</a></li>
                     <li class="nav-item"><a href="#profile-photos" class="nav-link" data-toggle="tab">PHOTOS</a></li>
                     <li class="nav-item"><a href="#profile-videos" class="nav-link" data-toggle="tab">VIDEOS</a></li>
                     <li class="nav-item"><a href="#profile-friends" class="nav-link" data-toggle="tab">FRIENDS</a></li>
                  </ul>
                  {/* END profile-header-tab */}
               </div>
            </div>
            {/* end profile */}
            {/*write comment section*/}
            <div class="input">
               <form action="">
                  <div class="input-group">
                     <input type="text" class="form-control rounded-corner" id="cmt" placeholder="Write a comment..."/>
                     <span class="input-group-btn p-l-10">
                     <button class="btn btn-primary f-s-12 rounded-corner" type="button" onClick={sendCmt}>Comment</button>
                     </span>
                  </div>
               </form>
            </div>
            {/*end comment sectiom*/}
            {/* begin profile-content */}
            <div class="profile-content">
               {/* begin tab-content */}
               <div class="tab-content p-0">
                  {/* begin #profile-post tab */}
                  <div class="tab-pane fade active show" id="profile-post">
                     {/* begin timeline */}
                     <ul class="timeline">
                        {Mapit()}
                     </ul>
                     {/* end timeline */}
                  </div>
                  {/* end #profile-post tab */}
               </div>
               {/* end tab-content */}
            </div>
            {/* end profile-content */}
         </div>
      </div>
   </div>
</div>
    )
}
