export default function Home (props) {

   let userName = props.user


   async function GetCmt () { // sends username, password from input server then creates s3 bucket in username and stores details on mongo


        // data to be sent in request to api includinb id of mongodb document
      let data = {Username: userName}

      // object containing fetch options for the put request
      let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }

      let Url = 'http://192.168.1.93:4001/getCmt'
      // post request using options object
      console.time("put response took");
      let response = await fetch(Url, options)
      let convert = await response.json ()
      console.timeEnd("put response took");
      console.log("got comment is:")
      console.log(convert)

      if ( response.status === 401 || response.status === 400){// handle not a match
        alert("couldnt find comments try again")
        // Navigate("/signIn")
        //return false   
       } else if ( response.status === 200){ // if password is a match return auth'd username
        console.log("found comment")
        console.log(convert.Posts)
        //let cmt=["hi", "hi"]
           //document.cookie = "authjwt="+convert.token; // store data in cookie
           //localStorage.setItem('jwt_token', convert.token) // Store JWT in storage
        //alert("Welcome to the club! Please setup your profile")
           //Navigate ("/profileSetup")
           return (
            convert.Posts.map((cmts)=> 
                     <li>
                  {/* begin timeline-time */}
                  <div class="timeline-time">
                     <span class="date">today</span>
                     <span class="time">04:20</span>
                  </div>
                  {/* end timeline-time */}
                  {/* begin timeline-icon */}
                  <div class="timeline-icon">
                     <a href="javascript:;">&nbsp;</a>
                  </div>
                     {/* end timeline-icon  */}
                  {/* begin timeline-body */}
                  <div class="timeline-body">
                     <div class="timeline-header">
                        <span class="userimage"><img src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic1.jpg"} alt=""/></span>
                        <span class="username"><a href="javascript:;">{userName}</a> <small></small></span>
                        <span class="pull-right text-muted">18 Views</span>
                     </div>
                     <div class="timeline-content">
                        <p>
                           {cmts}
                        </p>
                     </div>
                     <div class="timeline-likes">
                        <div class="stats-right">
                           <span class="stats-text">259 Shares</span>
                           <span class="stats-text">21 Comments</span>
                        </div>
                        <div class="stats">
                           <span class="fa-stack fa-fw stats-icon">
                           <i class="fa fa-circle fa-stack-2x text-danger"></i>
                           <i class="fa fa-heart fa-stack-1x fa-inverse t-plus-1"></i>
