import React from 'react';
import './css/main.css';
import './css/util.css';
import { useState, useEffect } from 'react'
import { Outlet, Link } from "react-router-dom";
import ChkAuth from './chkAuth';
import PrivateRoutes from './PrivateRoutes';
import PublicRoutes from './PublicRoutes';

const AuthorizationContext  = React.createContext();

//Checks if JWT stored in local storage is valid and shows private routes
//If auth fails then user is shown sign in page
function App() {

  const [sessionUser, setSessionUser] = useState (false)
  const [auth, setAuth] = useState (false);

  useEffect( () => {
    alert("useeffect")
    ChkAuth().then(user => {
      if (user) {
      setAuth(true)
      setSessionUser(user)
      };
    })
  },[sessionUser]);

  return (
  <div>
    <nav
      style={{
        borderBottom: "solid 1px",
        paddingBottom: "1rem"
      }}
    >
      <Link to="/signUp">signUp</Link> |{" "}
      <Link to="/signIn">signIn</Link>
      <Link to="/profileSetup">profileSetup</Link>
    </nav>
    <div className="limiter">
      <div className="container-login100" >
        <div className="wrap-login100 p-l-110 p-r-110 p-t-62 p-b-33">
          <form className="login100-form validate-form flex-sb flex-w">
            {/* <Outlet /> */}
            <AuthorizationContext.Provider value={sessionUser}>
                {alert("sessionUser is" , sessionUser)}
                {sessionUser ? <PrivateRoutes user={sessionUser}/> : <PublicRoutes />}
            </AuthorizationContext.Provider>
          </form>
        </div>
      </div>
    </div>
  </div>
  );
}

export default App;
