import { useEffect, useState} from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import ChkAuth from '../chkAuth';
import bkpic from '../images/profbkup.jpg';



export default function RenderProfileSetup () { 
  const [sessionUser, setSessionUser] = useState ("nonw")
  const [dp, setDp] = useState(false); // hide/show div
  const Navigate = useNavigate();
  var {user} = useParams()
  let userName = user



  useEffect( () => {
    ChkAuth().then(user => {
      if (user) {
      setSessionUser(user)
      } else {
         setSessionUser(false)
         alert("You need to sign in to view this page")
         Navigate("/signIn")
      }
    })
  },[]);


  const[cmt, setcmt] = useState({Posts:[], Photos: []})

  useEffect( () => {
     //alert("useeffect")
     getCmt().then(cmtz => {
       if (cmtz) {
       setcmt(cmtz)
       console.log("returned users data object", cmtz)
       }
     })
   },[]);

  function goToProfile (){
    Navigate ("/profile/x/"+sessionUser)
  }

  function Logout (){
    localStorage.removeItem('jwt_token')
    Navigate ("/")
  }

  async function getCmt () { // sends username, password from input then backend creates s3 bucket in username and stores details on mongo

    let data = {Username: userName, Data: ""}

    let options = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
    }

    let Url = 'http://192.168.1.93:4001/getCmt'
    let response = await fetch(Url, options)
    let convert = await response.json ()

    if ( response.status === 401 || response.status === 400){// handle not a match 
      console.log("error fetching, returned data is:", response)
    } else if ( response.status === 200){
      console.log("found users data:", convert)
      return convert
    }

  }


  async function updateBio () { 

    const bio = document.getElementById('bioBox').value;// get username, password and email.

    let data = {Username: userName, Updatetype: '$set',
                Key2updt: 'Bio', Value2updt: bio
    }

    let options = {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
    }

    let postUrl = 'http://192.168.1.93:4001/updatebio'
    let response = await fetch(postUrl, options)
    let convert = await response.json ()

    if ( response.status === 401 || response.status === 400){
      alert("your bio didnt save try again")
    } else if ( response.status === 200){
      console.log("saved bio")
      setcmt(convert) // store users data object
      setDp(!dp) // hide 
    }
  }

  function triggerClick(event){
    // clicking image triggers upload button click
    var myButton = document.getElementById(event.target.name);
    if ( myButton ) {
      myButton.click()
    }
  }

  // standard post
  async function addPhotos (event) {
    if (event) {
      let file = (event.target.files[0])
      var data = new FormData() 
      data.append('file', file) // add the file to the form
      data.append('user', userName)
      data.append('type', 'addPhotos')

      let options = {
        method: 'POST',
        body: data, 
      }
      //Create url to post file passing the username and pic number for storage
      let signinurl = 'http://192.168.1.93:4001/postfile/' + userName 

      // post the json
      let response = await fetch(signinurl, options)
      let convert = await response.json ()
      if ( response.status === 401 || response.status === 400){// handle not a match
        alert("your pic didnt save try again")
       } else if ( response.status === 200){ // if password is a match return auth'd username
        console.log("added pic to users photos")
        setcmt(convert)

       }

    }
  };

   async function handleSubmission (event) {
    if (event) {
      let file = (event.target.files[0])
      var data = new FormData() 
      data.append('file', file) // add the file to the form
      data.append('user', userName)
      data.append('type', 'profPic')

      let options = {
        method: 'POST',
        body: data, 
      }
      //Create url to post file passing the username and pic number for storage
      let signinurl = 'http://192.168.1.93:4001/postfile/' + userName

      // post the json
      let response = await fetch(signinurl, options)
      let convert = await response.json ()
      console.log(convert.Imgaddy)
      document.getElementById("profpic11").src = convert.Profpic // get posted img address and change profile picture

    }
  };

  return (
            <>
              <span className="login100-form-title p-b-53">
                Create your profile
              </span>
                {/*  <!--profpic1--> */}
                <div className="row">
                  <div class="col"></div>
                  <div class="col"></div>
                  <div class="col"></div>
                  <div class="col"></div>
                    <div className="col" >
                      <div className="profpics">
                        {cmt.Profpic ? <img className="profpics" id= "profpic11" name="profpic1"  onClick={triggerClick} alt="alt" src={cmt.Profpic}  
                          data-holder-Rendered="true"/> 
                          :
                          <img className="profpics" id= "profpic11" name="profpic1"  onClick={triggerClick} alt="alt" src={bkpic}  
                          data-holder-Rendered="true"/>
                        }
                      </div>
                    </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic1" type="file" className="blocked" onChange={handleSubmission}  name= "uploader1"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic2--> */}
                      <div className="col-md-4 mb-3" >




                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic2" type="file" className="blocked" onChange={addPhotos}  name= "uploader2"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>

                </div>                   
              <div className="p-t-13 p-b-9">

              </div>
              <div className="wrap-input100 validate-input">{cmt.Bio ? cmt.Bio : "You haven't added a bio yet. Click to add one now!"}</div>

              <a href="#" className="btn btn-sm btn-info mb-2" onClick={() => {setDp(!dp)}}>Edit Bio</a>
              {dp && 
              <>
                <div className="wrap-input100 validate-input" style={{ display: dp }}>
                  ​<span className="txt10">
                    Max 70 characters
                  </span> 
                  <span><textarea maxlength="70" id="bioBox" rows="10" cols="10"></textarea></span>
                </div>

                <div className="container-login100-form-btn m-t-17">
                  <button className="login100-form-btn" type="button" onClick={() =>{updateBio()}}>
                    Save Changes
                  </button>
                </div>
              </>
              }
              {sessionUser === user ?
              <div className="w-full text-center p-t-55">
                <div className="connected-container">
                  <div className="gif-grid">
                  {/* Map through gifList instead of TEST_GIFS */}
                      {cmt.Photos.map((pic) => (
                        <div className="gif-item" key={pic}>
                            <img src={pic} alt={pic} />
                        </div>
                      ))}
                  </div>
                </div>
              </div>

              : 
              <div></div> 
              }
              <div>
                <a href="#" className="btn btn-sm btn-info mb-2" onClick={() => {triggerClick()}}>Add Photo</a>

                {/* <img className="addPhoto"  id= "profpic21" name="profpic2" onClick={triggerClick} alt="100x100" src={addPhotoIcon} 
                          data-holder-Rendered="true"/> */}
              </div> 
              <div className="w-full text-center p-t-55">
              <a href="#" className="btn btn-sm btn-info mb-2" onClick={() => {goToProfile()}}>View Profile</a>
              <div></div>
              <a href="#" className="btn btn-sm btn-info mb-2" onClick={() => {Logout()}}>Log Out</a>

              </div>
              <div>

              </div>
            </>
          )
}
