import { useEffect, useState} from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import ChkAuth from '../chkAuth';



export default function RenderProfileSetup (props) { 
  //alert("called setup")
  //UseState to hold uploaded file for sending
  const [sessionUser, setSessionUser] = useState ("nonw")
  const [auth, setAuth] = useState (false);
  const Navigate = useNavigate();
  let userName = sessionUser



  useEffect( () => {
    //alert("useeffect")
    ChkAuth().then(user => {
      if (user) {
      setAuth(true)
      setSessionUser(user)
      //alert("user returnedddddd from chkauth", user)
      } else {
         setAuth(false)
         setSessionUser(false)
         alert("You need to sign in to view this page")
         Navigate("/signIn")
      }
    })
  },[]);

  function Logout (){
    localStorage.removeItem('jwt_token')
    //alert("deleted jwt")
    Navigate ("/")
  }


  async function updateBio () { // sends username, password from input server then creates s3 bucket in username and stores details on mongo

    const bio = document.getElementById('bioBox').value;// get username password and email

    const Post = { // send starter post
      Username : userName,
      MainCmt : bio ,// get username password and email
      Time : new Date().toLocaleTimeString('en-GB', { hour: "numeric", minute: "numeric"}),
      TimeStamp : Date.now()
   }

      // data to be sent in request to api includinb id of mongodb document
    let data = {Username: userName, Updatetype: '$set',
                isReply: 'Bio', Value2updt: Post
    }

    // object containing fetch options for the put request
    let options = {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(data),
    }

    let postUrl = 'http://192.168.1.93:4001/updatebio'
    // post request using options object
    console.time("put response took");
    let response = await fetch(postUrl, options)
    let convert = await response.json ()
    console.timeEnd("put response took");
    console.log("Put completed updated document is:")
    console.log(convert)

    if ( response.status === 401 || response.status === 400){// handle not a match
      alert("your bio didnt save try again")
      // Navigate("/signIn")
      //return false   
     } else if ( response.status === 200){ // if password is a match return auth'd username
      console.log("saved bio, redirecting to profile page")
      //alert("saved")
			//document.cookie = "authjwt="+convert.token; // store data in cookie
			//localStorage.setItem('jwt_token', convert.token) // Store JWT in storage
      //alert("Welcome to the club! Please setup your profile")
			Navigate ("/profile/"+sessionUser)
     }
  }


  function triggerClick(event){
    // clicking image triggers upload button click
    var myButton = document.getElementById(event.target.name);
    if ( myButton ) {
      myButton.click()
    }
  }

  // standard post
  async function handleSubmission (event) {
    if (event) {
      let file = (event.target.files[0])
      let Picnum = event.target.id
      var data = new FormData() 
      data.append('file', file) // add the file to the form
      data.append('user', userName)

      let options = {
        method: 'POST',
        body: data, 
      }
      //Create url to post file passing the username and pic number for storage
      let signinurl = 'http://192.168.1.93:4001/postfile/' + userName + "/" + Picnum

      // post the json
      let response = await fetch(signinurl, options)
      let convert = await response.json ()
      console.log(convert.Imgaddy)
      let newimageurl = convert.Imgaddy // get posted img address and change profile picture
      let pic2Replace = Picnum + "1"
      document.getElementById(pic2Replace).src = newimageurl;
    }
  };

  return (
            <>
              <span className="login100-form-title p-b-53">
                Create your profile
              </span>
                {/*  <!--profpic1--> */}
                <div className="row" align="center">
                    <div className="col-md-4 mb-3" >
                      <div className="profpics">
                        <img className="profpics" id= "profpic11" name="profpic1"  onClick={triggerClick} alt="alt" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic1.jpg"}
                          data-holder-Rendered="true"/>
                      </div>


                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic1" type="file" className="blocked" onChange={handleSubmission}  name= "uploader1"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic2--> */}
                      <div className="col-md-4 mb-3" >
                      <div className="profpics">
                        <img className="profpics"  id= "profpic21" name="profpic2" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic2.png"}
                          data-holder-Rendered="true"/>
                                </div>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic2" type="file" className="blocked" onChange={handleSubmission}  name= "uploader2"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic3--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="profpics"  id= "profpic31" name="profpic3" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic3.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic3" type="file" className="blocked" onChange={handleSubmission}  name= "uploader3"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic4--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="profpics"  id= "profpic41" name="profpic4" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic4.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic4" type="file" className="blocked" onChange={handleSubmission}  name= "uploader4"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic5--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="profpics"  id= "profpic51" name="profpic5" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic5.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic5" type="file" className="blocked" onChange={handleSubmission}  name= "uploader5"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic6--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="profpics"  id= "profpic61" name="profpic6" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic6.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
