export default async function Chkauth (){//check if jwt is valid 
    let jwt = localStorage.getItem('jwt_token');

    let jwtObject = {
      Data1: jwt
    }

    let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(jwtObject),
      }

      let url = 'http://192.168.1.93:4001/chkauth'
      let response = await fetch(url, options)
      let convert = await response.json ()
      console.log("User password check complete:")
      console.log(response.status)

    if ( response.status === 401 || response.status === 400){// handle not a match
      //alert("you not authorized to view this page please sign in")
      //Navigate("/signIn")
      return false   
     } else if ( response.status === 200){ // if password is a match return auth'd username
      //alert("user found chkauth returning user")
      console.log("authorized by api", convert.AuthdUser)
      return convert.AuthdUser
     }
}
