//import logo from './logo.svg';
//import './App.css';
import React from 'react';
import './css/main.css';
import './css/util.css';
import { useState, useEffect } from 'react'
import { Outlet, Link } from "react-router-dom";


function App() {
  //conditions for Renders use tokens
  const signedUp = true
  const signedIn = true
  const accountCreated = false
  let sessionUser = null

  //UseState to hold uploaded file for sending
  const [selectedFile, setSelectedFile] = useState();
  const [auth, setAuth] = useState (false);

  function triggerClick(){

    // clicking image triggers upload button click
    var myButton = document.getElementById("profpic1");
    if ( myButton ) {
      myButton.click()
    }
  }

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
    handleSubmission()
  };

  // standard post
  async function handleSubmission () {
    const formData = new FormData();
    //selectedFile.name = picNum

    formData.append('File', selectedFile);

    let options = {
      method: 'POST',
      body: formData, // send the form with the file and user data
      }

      let signinurl = 'http://86.148.245.236:3000/upload'// get username from cookie?

      // post the json
     // console.time("deleted response took");
      let response = await fetch(signinurl, options)
      let convert = await response.json ()
      //console.timeEnd("deleted response took");
      console.log(convert.Imgaddy)
  };

  async function signup () { // sends userbame password from input(need!) server creates bucket in username and stores details on mongo

