import { useState } from "react";

export default function RenderSignin () {
	const [Render, SetRender] = useState (0);
  	alert(Render)

	async function signin(){ // Sign in check password get token
		const username = document.getElementById('username').value;// get username and password
		const password = document.getElementById('pass').value;
		//console.log(username, password)

		let signindata = {
			Username: username,
			Password: password
		}

		let options = {
		method: 'POST',
		headers: {
			'Content-Type': 'application/json',
		},
		body: JSON.stringify(signindata),
		}

		let signinurl = 'http://192.168.1.93:4001/signin/' + username

		// post request to check sign in details
		//console.time("signin response took");
		let response = await fetch(signinurl, options)// post signin details with id in url
		let convert = await response.json ()
		//console.timeEnd("signin response took");
		console.log("User password check complete:")
		console.log(response.status)
		if ( response.status === 200){ // if password is a match redirect to profile page
			console.log("pword correct cookie is", "authjwt="+convert.token)
			//document.cookie = "authjwt="+convert.token; // store data in cookie needs to be http only do later!
			localStorage.setItem('jwt_token', convert.token)
			//SetRender(Render+1)
			//chkauth()
			window.location.href = "http://http://localhost:3000/";
		} else if ( response.status === 401){// handle not a match
			alert("userame or password was incorrect please try again")
		}

		}

  return (  
    <>
      <span className="login100-form-title p-b-53">
						Sign In With
					</span>

					<a href="/#" className="btn-face m-b-20">
						<i className="fa fa-facebook-official"></i>
						Facebook
					</a>

					<a href="/#" className="btn-google m-b-20">
						<img src="images/icons/icon-google.png" alt="GOOGLE" />
						Google
					</a>

					<div className="p-t-31 p-b-9">
						<span className="txt1">
							Username
						</span>
					</div>
					<div className="wrap-input100 validate-input" data-validate = "Username is required">
						<input className="input100" type="text" name="username" id="username"/>
						<span className="focus-input100"></span>
					</div>

					<div className="p-t-13 p-b-9">
						<span className="txt1">
							Password
						</span>

						<a href="/#" className="txt2 bo1 m-l-5">
							Forgot?
						</a>
					</div>
					<div className="wrap-input100 validate-input" data-validate = "Password is required">
						<input className="input100" type="password" name="pass" id="pass" />
						<span className="focus-input100"></span>
					</div>

					<div className="container-login100-form-btn m-t-17">
						<button className="login100-form-btn" type="button" onClick={signin}>
							Sign In
						</button>
					</div>

					<div className="w-full text-center p-t-55">
						<span className="txt2">
							Not a member?
						</span>

						<a href="signup.html" className="txt2 bo1">
							Sign up now
						</a>
					</div>
    </>
  )
};
