import { useState, useEffect } from "react"

export default function Home (props) {

   let userName = props.user
   const[cmt, setcmt] = useState(["hi", "hi"])
   useEffect( () => {
      alert("useeffect")
      getCmt().then(cmtz => {
        if (cmtz) {
        setcmt(cmtz)
        console.log("returned from chkauth added to statw", cmtz)
        }
      })
    },[]);


   async function getCmt () { // sends username, password from input server then creates s3 bucket in username and stores details on mongo


        // data to be sent in request to api includinb id of mongodb document
      let data = {Username: userName}

      // object containing fetch options for the put request
      let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }

      let Url = 'http://192.168.1.93:4001/getCmt'
      // post request using options object
      console.time("put response took");
