import { useEffect, useState} from 'react'
import { useNavigate } from 'react-router-dom';


export default function RenderProfileSetup (props) { 
  //UseState to hold uploaded file for sending
  const [render, setForceRender] = useState(1);
  const Navigate = useNavigate();
  let userName = props.user

  function Logout (){
    localStorage.removeItem('jwt_token')
    alert("deleted jwt")
    useEffect(() => {
      Navigate("/signIn")
    }

    )


  }

  function triggerClick(event){
    // clicking image triggers upload button click
    var myButton = document.getElementById(event.target.name);
    if ( myButton ) {
      myButton.click()
    }
  }

  // standard post
  async function handleSubmission (event) {
    if (event) {
      let file = (event.target.files[0])
      let Picnum = event.target.id
      var data = new FormData() 
      data.append('file', file) // add the file to the form
      data.append('user', userName)

      let options = {
        method: 'POST',
        body: data, 
      }
      //Create url to post file passing the username and pic number for storage
      let signinurl = 'http://192.168.1.93:4001/postfile/' + userName + "/" + Picnum

      // post the json
      let response = await fetch(signinurl, options)
      let convert = await response.json ()
      console.log(convert.Imgaddy)
      let newimageurl = convert.Imgaddy // get posted img address and change profile picture
      let pic2Replace = Picnum + "1"
      document.getElementById(pic2Replace).src = newimageurl;
    }
  };

  return (
            <>
              <span className="login100-form-title p-b-53">
                Create your profile
              </span>
                {/*  <!--profpic1--> */}
                <div className="row" align="center">
                    <div className="col-md-4 mb-3" >
                      <div className="rounded-circle z-depth-2">
                        <img className="rounded-circle z-depth-2" id= "profpic11" name="profpic1" style={{width: "100%", height: "100%"}} onClick={triggerClick} alt={"100x100"} src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic1.png"}
                          data-holder-Rendered="true"/>
                      </div>


                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic1" type="file" className="blocked" onChange={handleSubmission}  name= "uploader1"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic2--> */}
                      <div className="col-md-4 mb-3" >
                      <div className="rounded-circle z-depth-2">
                        <img className="rounded-circle z-depth-2"  id= "profpic21" name="profpic2" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic2.png"}
                          data-holder-Rendered="true"/>
                                v</div>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic2" type="file" className="blocked" onChange={handleSubmission}  name= "uploader2"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic3--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="rounded-circle z-depth-2"  id= "profpic31" name="profpic3" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic3.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic3" type="file" className="blocked" onChange={handleSubmission}  name= "uploader3"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic4--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="rounded-circle z-depth-2"  id= "profpic41" name="profpic4" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic4.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic4" type="file" className="blocked" onChange={handleSubmission}  name= "uploader4"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic5--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="rounded-circle z-depth-2"  id= "profpic51" name="profpic5" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic5.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic5" type="file" className="blocked" onChange={handleSubmission}  name= "uploader5"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic6--> */}
                      <div className="col-md-4 mb-3" >
                    <img className="rounded-circle z-depth-2"  id= "profpic61" name="profpic6" onClick={triggerClick} alt="100x100" src={"https://" + userName + "ajh46unique.s3.us-east-2.amazonaws.com/profpic6.png"}
                      data-holder-Rendered="true"/>

                  </div>
                  <div className="visually-hidden">
                        {/* <!--the picture click triggers a click of find file button using its name prop to pass to func and use as id then same for onChange to upload button.. chain of name = next elements id--> */}

                            <div>
                                  <input id="profpic6" type="file" className="blocked" onChange={handleSubmission}  name= "uploader6"/>
                            </div>
                            {/* <button type="button" id="uploader1" name="profpic1" onClick="(uploadpic(this.name))" className="blocked">Upload</button> */}

                    </div>
                      {/* <!--profpic6--> */}
                </div>                   
              <div className="p-t-13 p-b-9">
              </div>
              <div className="wrap-input100 validate-input">
                <input className="bioBox" type="text" name="pass" id="pass" />
                ​
                <span className="focus-input100"><textarea id="txtArea" rows="10" cols="70"></textarea></span>
              </div>

              <div className="container-login100-form-btn m-t-17">
                <button className="login100-form-btn" type="button" onClick="signin()">
                  Sign In
                </button>
              </div>
              <div className="w-full text-center p-t-55">
                <span className="txt2">
                  Not a member?
                </span>

                {/* <a href="signup.html" className="txt2 bo1"> */}
                <button onClick={() => {Logout()}} className="txt2 bo1" >
                  logout
                </button>
              </div>
            </>
          )
}
