import { useState} from 'react'

export default function RenderProfileSetup (props) { 
  //UseState to hold uploaded file for sending
  const [render, setForceRender] = useState(1);
  //alert(Picnum)

  let userName = props.user

  /* chkAuth().then(user => {
    if (user) {
      userName = user
      console.log("urlchk https://" + userName + "4hxt0n.s3.us-east-2.amazonaws.com/profpic1.png")
    };
  }) */

  function triggerClick(event){

    // clicking image triggers upload button click
    var myButton = document.getElementById(event.target.name);
    if ( myButton ) {
      myButton.click()
    }
  }

  /* const changeHandler = (event) => {
    if (event) {
      setSelectedFile(event.target.files[0])
      handleSubmission()
    }

  }; */

  // standard post
  async function handleSubmission (event) {
    if (event) {
      let file = (event.target.files[0])
      let Picnum = event.target.id
      //let Picnum = "profpic1"
      //var input = document.getElementById(Picnum)// get file thats been given
      var data = new FormData() // create empty form data cos api reads form for the file..coulda done this.form but gave errors
      data.append('file', file) // add the file to the form
      data.append('user', userName)

      let options = {
      method: 'POST',
      body: data, // send the form with the file and user data
      }
      //let userName = chkAuth()
      let signinurl = 'http://192.168.1.93:4001/postfile/' + userName + "/" + Picnum

      // post the json
      // console.time("deleted response took");
      let response = await fetch(signinurl, options)
      let convert = await response.json ()
      //console.timeEnd("deleted response took");
      console.log(convert.Imgaddy)
      //setForceRender(render + 1)
      let newimageurl = convert.Imgaddy // get posted img address and change profile picture
      let pic2Replace = Picnum + "1"
      document.getElementById(pic2Replace).src = newimageurl;
    }

/* 
    const formData = new FormData();
    //selectedFile.name = Picnum

    formData.append('File', selectedFile);

