import { useState, useEffect } from "react"
import ChkAuth from '../chkAuth';
import makePost from './profileComponents/makePost';

export default function Home (props) {
   const [sessionUser, setSessionUser] = useState (false)
   const [auth, setAuth] = useState (false);
   //const Navigate = useNavigate();

  useEffect( () => {
    alert("useeffect")
    ChkAuth().then(user => {
      if (user) {
      setAuth(true)
      setSessionUser(user)
      alert("user returned from chkauth", user)
      }
    })
  },);


   let userName = props.user
   const[cmt, setcmt] = useState(["hi", "hi"])
   useEffect( () => {
      alert("useeffect")
      getCmt().then(cmtz => {
        if (cmtz) {
        setcmt(cmtz)
        console.log("returned from chkauth added to statw", cmtz)
        }
      })
    },[]);



   async function sendCmt (msgType, iD) { // sends username, password from input server then creates s3 bucket in username and stores details on mongo

      const Post = { // send starter post
         MainCmt : document.getElementById('cmt').value ,// get username password and email
         Time : new Date().toLocaleTimeString('en-GB', { hour: "numeric", minute: "numeric"}),
         Date : new Date().toLocaleDateString(),
         Comments : [] ,// begin sub comments 
         Likes : 0 // begin like
      }

      let reply = document.getElementById(iD).value // get reply if reply cmt is there

        // dsend start comment or reply to comment depending on is replychk false/true
      let data = {Username: userName, Updatetype: '$set',
                  Key2updt: 'Posts', isReply: msgType, replyCmt: reply, PostIndx: iD, Value2updt: Post
      }

      // object containing fetch options for the put request
