//import logo from './logo.svg';
//import './App.css';
import React from 'react';
import './css/main.css';
import './css/util.css';
import { useState, useEffect } from 'react'
import { Outlet, Link } from "react-router-dom";
import chkAuth from './chkAuth';


function App() {

  //conditions for Renders use tokens
/*   const signedUp = true
  const signedIn = true
  const accountCreated = false */
  const [sessionUser, setSessionUser] = useState (null)
  const [auth, setAuth] = useState (false);

  async function chkauth (){//chk if jwt is valied for page used by each html on every load
    let jwt = localStorage.getItem('jwt_token');
    console.log("got from storage:", jwt)

    let jwtObject = {
      Data1: jwt
    }

    let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(jwtObject),
      }

      let url = 'http://192.168.1.93:4001/chkauth'

      // post request to check sign in details
      //console.time("signin response took");
      let response = await fetch(url, options)// post jwt
      let convert = await response.json ()
      //console.timeEnd("signin response took");
      console.log("User password check complete:")
      console.log(response.status)

    //response = post(jwt, "chkauth")
    if ( response.status === 401){// handle not a match
      alert("you not authorized to view this page please sign in")
      //window.location.href = "signin.html";     
     } else if ( response.status === 200){ // if password is a match redirect to profile page
      console.log("authorized by api", convert.AuthdUser)
      setAuth(true)
      setSessionUser = convert.AuthdUser
     }
} 

  useEffect( () => {
    //const user = 
    alert("mounted")
    chkAuth();

    // if (user) {

    // }
  });

  return (
  <div>
    <nav
      style={{
        borderBottom: "solid 1px",
        paddingBottom: "1rem"
      }}
    >
      <Link to="/signUp">signUp</Link> |{" "}
      <Link to="/signIn">signIn</Link>
      <Link to="/profileSetup">profileSetup</Link>
    </nav>
    <div className="limiter">
      <div className="container-login100" >
        <div className="wrap-login100 p-l-110 p-r-110 p-t-62 p-b-33">
          <form className="login100-form validate-form flex-sb flex-w">
            <Outlet />
          </form>
        </div>
      </div>
    </div>
  </div>
  );
}

export default App;
