import React from 'react';
import { useState, useEffect } from "react"
import { useNavigate, useParams } from "react-router";
import ChkAuth from '../chkAuth';
import SendData from './SendData';
import '../css/App1.css';
//import '../css/main.css';
//import '../css/util.css';

export default function Home (props) {
   const [sessionUser, setSessionUser] = useState ("nonw")
   const [auth, setAuth] = useState (false);
   const [viewReply, setviewReply] = useState(false); // use to show comments when clicked
   const [viewLikes, setviewLikes] = useState(false); // use to show comments when clicked
   const Navigate = useNavigate();

   var {Page, user} = useParams()
   //alert("userdataid is", page, user)
   let flexUser
   if (Page === "all"){
     flexUser = "Home Feed"
   } else { 
      flexUser = Page 
   }
   let Today = new Date().toLocaleDateString()

  useEffect( () => {
    //alert("useeffect")
    ChkAuth().then(user => {
      if (user) {
      setAuth(true)
      setSessionUser(user)
      //alert("user returned from chkauth", user)
      } else {
         setAuth(false)
         setSessionUser(false)
         alert("You need to sign in to view this page")
         Navigate("/signIn")
      }
    })
  },[]);


   const[cmt, setcmt] = useState({Posts:[], Photos: []})
   useEffect( () => {
      //alert("useeffect")
      getCmt().then(cmtz => {
        if (cmtz) {
        setcmt(cmtz)
        console.log("returned from chkauth added to statw", cmtz)
        }
      })
    },[]);

    function goToProfile (){
      Navigate ("/profile/x/"+user)
    }

    function goToAllPosts (){
      Navigate ("/profile/all/"+user)
    }

    function goToPhotos () {
       if (Page !== "media") { // for when your on media tab already
         if (Page === "all") {
            Navigate("/profile/media/"+user)
         } else {
            Navigate("/profile/media/"+user)
         }
       }
    }

    function Logout (){
      localStorage.removeItem('jwt_token')
      //alert("deleted jwt")
      Navigate ("/")
    }

   async function getCmt (replyPoster) { // sends username, password from input server then creates s3 bucket in username and stores details on mongo


        // data to be sent in request to api includinb id of mongodb document
      let data = {Page: Page, UserName: user,}

      if (replyPoster) {data.Username = replyPoster}

      // object containing fetch options for the put request
      let options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
      }

      let Url = 'http://192.168.1.93:4001/getCmt'
      // post request using options object
      console.time("put response took");
      let response = await fetch(Url, options)
      let convert = await response.json ()
      console.timeEnd("put response took");
      console.log("got comment is:")
      console.log(convert)

      if ( response.status === 401 || response.status === 400){// handle not a match
        //alert("couldnt find comments try again")
        // Navigate("/signIn")
        //return false   
       } else if ( response.status === 200){ // if password is a match return auth'd username
        console.log("found post data")
        console.log(convert.Posts)
        //setcmt(convert.Posts)
           //document.cookie = "authjwt="+convert.token; // store data in cookie
           //localStorage.setItem('jwt_token', convert.token) // Store JWT in storage
        ////alert("Welcome to the club! Please setup your profile")
           //Navigate ("/profileSetup")
           return convert
       }

    }


   async function sendCmt (msgType, cmtAuthr, iD) { // sends username, password from input server then creates s3 bucket in username and stores details on mongo
      //alert("date is", Date.now)
      let cmt = "no need for comment"// in case is reply and comment box not shown so cause null error
      if (msgType === "isCmt") {
         cmt = document.getElementById("cmt").value // get reply to cmt if there
      }

      const Post = { // send starter post
         Username : cmtAuthr,
         MainCmt : cmt ,// get username password and email
         Time : new Date().toLocaleTimeString('en-GB', { hour: "numeric", minute: "numeric"}),
         Date : Today,
         TimeStamp : Date.now(),
         Comments : [] ,// begin sub comments 
         Likes : [] // begin like
      }

      let reply ={Username : "dud",
                  Comment : "dud",
                  Profpic : "dud"
      }

      //let reply = "hi"
      if (msgType === "isResponse") {
         reply.Username = sessionUser
         reply.Comment = document.getElementById(iD).value // get reply to cmt if there console.log("returned user data is", repliersData.Profpic);
         getCmt(sessionUser).then((repliersData)=> { reply.Profpic = repliersData.Profpic; SendData(Page, msgType, reply, Liker, iD, Post).then((convert)=> convert ? setcmt(convert) :  console.log("error") ); })
      }

      let Liker = {Username : "dud" ,
                  Profpic : "dud"
      }
      if (msgType === "cmtLiked") {
         //alert("likesender is", sessionUser)
         Liker.Username = sessionUser
         getCmt(sessionUser).then((repliersData)=> {Liker.Profpic = repliersData.Profpic; SendData(Page, msgType, reply, Liker, iD, Post).then((convert)=> convert ? setcmt(convert) :  console.log("error") ); })
      }

      if (msgType === "cmtLiked") {
         //alert("likesender is", sessionUser)
         Liker.Username = sessionUser
         getCmt(sessionUser).then((repliersData)=> {Liker.Profpic = repliersData.Profpic; SendData(Page, msgType, reply, Liker, iD, Post).then((convert)=> convert ? setcmt(convert) :  console.log("error") ); })
      }

      if (msgType === "isCmt") {
         getCmt(sessionUser).then((repliersData)=> {Liker.Profpic = repliersData.Profpic; SendData(Page, msgType, reply, Liker, iD, Post).then((convert)=> convert ? setcmt(convert) :  console.log("error") ); })
      }

    }

   function redirecter () {
      Navigate("/signIn")
   }

   function EditProfile (){
      Navigate ("/editProfile/"+sessionUser)
    }



return (

<div className="container">
   <div className="row">
      <div className="col-md-12">
         <div id="content" className="content content-full-width">
            {/* begin profile */}
            <div className="profile">
               <div className="profile-header">
                  {/* BEGIN profile-header-cover */}
                  <div className="profile-header-cover"></div>
                  {/* END profile-header-cover */}
                  {/* BEGIN profile-header-content */}
                  <div className="profile-header-content">
                     {/* BEGIN profile-header-img */}
                     <div className="profile-header-img">
                        <img calssName="profpics" src={cmt.Profpic} alt=""/>
                     </div>
                     {/* END profile-header-img */}
                     {/* BEGIN profile-header-info */}
                     <div className="profile-header-info">
                        <h4 className="m-t-10 m-b-5">{user}</h4>
                        <p className="m-b-10">UXUI + Frontend Developer</p>
                        <a href="#" className="btn btn-sm btn-info mb-2" onClick={() => {EditProfile()}}>Edit Profile</a>
                        <a href="#" className="btn btn-sm btn-info mb-2" onClick={() => {Logout()}}>Log Out</a>
                     </div>
                     {/* END profile-header-info */}
                  </div>
                  {/* END profile-header-content */}
                  {/* BEGIN profile-header-tab */}
                  <ul className="profile-header-tab nav nav-tabs">
                     <li className="nav-item"><a href="" className="nav-link active show" data-toggle="tab" onClick={() => {goToProfile()}}>PROFILE</a></li>
                     <li className="nav-item"><a href="" className="nav-link" data-toggle="tab" onClick={() => {goToAllPosts()}}>FEED</a></li>
                     <li className="nav-item"><a href="" className="nav-link" data-toggle="tab" onClick={() => {goToPhotos()}}>MEDIA</a></li>
                     <li className="nav-item"><a href="#profile-friends" className="nav-link" data-toggle="tab">FRIENDS</a></li>
                  </ul>
                  {/* END profile-header-tab */}
               </div>
            </div>
            {/* end profile */}
            {/*show media page */}
            {sessionUser && Page === "media" ? 
            <div className="connected-container">
               <div className="gif-grid">
               {/* Map through gifList instead of TEST_GIFS */}
                  {cmt.Photos.map((pic) => (
                     <div className="gif-item" key={pic}>
                        <img src={pic} alt={pic} />
                     </div>
                  ))}
               </div>
            </div>
            : 
            <div></div>}
            {/*render users profile page or feed showing all comments*/}
            {(sessionUser === user && Page !== "media") || (sessionUser && Page === "all") ? <div className="input">
                     <form action="">
                        <div className="input-group">
                           <input type="text" className="form-control rounded-corner" id="cmt" placeholder="Write a comment..."/>
                           <span className="input-group-btn p-l-10">
                              <button className="btn btn-primary f-s-12 rounded-corner" type="button" onClick={() => sendCmt("isCmt", sessionUser, 0)}>Comment</button>
                           </span>
                        </div>
                  </form>
              </div> : <div></div>}
            {/*end comment sectiom*/}
            {/* begin profile-content */}
            <div className="profile-content">
               {/* begin tab-content */}
               <div className="tab-content p-0">
                  {/* begin #profile-post tab */}
                  <div className="tab-pane fade active show" id="profile-post">
                     {/* begin timeline */}
                     <ul className="timeline">
                     { Page !== "media" ? 
                        cmt.Posts.map((userData)=> (
                           <li>
                           {/* begin timeline-time */}
                           <div className="timeline-time">
                              <span className="date">{userData.Date === Today ? "Today" : userData.Date}</span>
                              <span className="time">{userData.Time}</span>
                           </div>
                           {/* end timeline-time */}
                           {/* begin timeline-icon */}
                           <div className="timeline-icon">
                              <a href="javascript:;">&nbsp;</a>
                           </div>
                              {/* end timeline-icon  */}
                           {/* begin timeline-body */}
                           <div className="timeline-body">
                              <div className="timeline-header">
                                 <span className="userimage"><img src={cmt.Profpic} alt=""/></span>
                                 <span className="username"><a href="javascript:;">{userData.Username}</a> <small></small></span>
                                 <span className="pull-right text-muted">18 Views</span>
                              </div>
                              <div className="timeline-content">
                                 <p>
                                    {userData.MainCmt}
                                 </p>
                              </div>
                              <div className="timeline-likes">
                                 <div className="stats-right">
                                    <span className="stats-text" onClick={() => {setviewLikes(!viewLikes)}}>{userData.Likes?.length} Likes</span>
                                    {console.log("user likes eturned is", userData.Likes)}
                                    {viewLikes &&
                                      userData.Likes.map((Likes)=> (
                                       <div className="timeline-header">
                                       {<span className="userimage"><img src={Likes.Profpic} alt=""/></span> }
                                       <a href= {"http://localhost:3000/Profile/x/"+Likes.Username}>{Likes.Username}</a> 
                                       </div>
                                      ))
                                    } 
                                    <span className="stats-text" onClick={() => {setviewReply(!viewReply)}}>{userData.Comments?.length} Comments</span>
                                    {viewReply &&
                                      userData.Comments.map((replys)=> (
                                       <div className="timeline-header">
                                       {<span className="userimage"><img src={replys.Profpic} alt=""/></span> }
                                       <span ><a href= {"http://localhost:3000/Profile/x/"+replys.Username}>{replys.Username}</a> <small></small></span>
                                       <div></div>
                                          <span className="pull-right text-muted">{replys.Comment}</span>
                                       </div>
                                      ))
                                    } 
                                 </div>
                                 <div className="stats">
                                    <span className="fa-stack fa-fw stats-icon">
                                    <i className="fa fa-circle fa-stack-2x text-danger"></i>
                                    <i className="fa fa-heart fa-stack-1x fa-inverse t-plus-1" onClick={()=>sendCmt("cmtLiked", userData.Username, userData.PostNum)}></i>
                                    </span>
                                    {/* <span className="fa-stack fa-fw stats-icon">
                                    <i className="fa fa-circle fa-stack-2x text-primary"></i>
                                    <i className="fa fa-thumbs-up fa-stack-1x fa-inverse"></i>
                                    </span> */}
                                    {/* <span className="stats-total">4.3k</span> */}
                                 </div>
                              </div>
                              <div className="timeline-comment-box">
                                 {/* <div className="user"><img src={cmt.Profpic}/></div> */}
                                 <div className="input">
                                    <form action="">
                                       {auth ? 
                                          <div className="input-group">
                                          <input type="text" className="form-control rounded-corner" id={userData.PostNum} placeholder="Write a comment..."/>
                                          <span className="input-group-btn p-l-10">
                                             <button className="btn btn-primary f-s-12 rounded-corner" type="button"  onClick={()=>sendCmt("isResponse", userData.Username, userData.PostNum)}>Comment</button>
                                          </span>
                                          </div>
                                          :
                                          <div className="input-group">
                                          <input type="text" className="form-control rounded-corner" id={userData.PostNum} placeholder="Write a comment..."/>
                                          <span className="input-group-btn p-l-10">
                                             <button className="btn btn-primary f-s-12 rounded-corner" type="button" onClick={()=>redirecter()} >Sign In to Commment</button>
                                          </span>
                                          </div>}
                                    </form>
                                 </div>
                              </div>
                           </div>
                           {/* end timeline-body */}
                        </li>
                        ))
                        : 
                         <div></div>
                     }
                     </ul>
                     {/* end timeline */}
                  </div>
                  {/* end #profile-post tab */}
               </div>
               {/* end tab-content */}
            </div>
            {/* end profile-content */}
         </div>
      </div>
   </div>
</div>
    )
}
